import React, { useState, useEffect } from "react";
import {
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Table,
  TableBody,
} from "@mui/material";
import Paper from "@mui/material/Paper";
import "./OrderBook.css";

const OrderBook = ({ coin }) => {
  const [bids, setBids] = useState([]);
  const [asks, setAsks] = useState([]);

  useEffect(() => {
    const subscribe = {
      type: "subscribe",
      product_ids: [coin],
      channels: ["level2_batch"],
    };
    const ws = new WebSocket("wss://ws-feed.exchange.coinbase.com");

    ws.onopen = () => {
      ws.send(JSON.stringify(subscribe));
    };
    ws.onmessage = (event) => {
      const response = JSON.parse(event.data);

      //de donde sale este change?

      response?.changes?.map((change) => {
        change[0] === "buy"
          ? setBids([
              ...bids.slice(0, 5),
              { price: change[1], size: change[2] },
            ])
          : setAsks([
              ...asks.slice(0, 5),
              { price: change[1], size: change[2] },
            ]);
      });
    };
    ws.onclose = () => {
      ws.close();
    };

    return () => {
      ws.close();
    };
  }, [bids, asks]);

  useEffect(() => {
    setBids([]);
    setAsks([]);
  }, [coin]);
  const filtered = change.filter((isPositive) => isPositive != 0);
  console.log(filtered);
  return (
    <TableContainer component={Paper} className="table--parent">
      <Table
        sx={{ minWidth: 300, width: 100 }}
        aria-label="simple table"
        className="table--child"
      >
        <TableHead>
          <TableRow>
            <TableCell align="center">Price (USD)</TableCell>
            <TableCell align="center">Market Size</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {bids?.map((bid, index) => {
            return (
              <TableRow
                key={index}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell sx={{ color: "error.main" }} align="center">
                  {bid.price}
                </TableCell>
                <TableCell align="center">{bid.size}</TableCell>
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
      <Table
        sx={{ minWidth: 300, width: 100 }}
        aria-label="simple table"
        className="table--child"
      >
        <TableHead>
          <TableRow>
            <TableCell align="center">Price (USD)</TableCell>
            <TableCell align="center">Market Size</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {asks?.map((asks, index) => {
            return (
              <TableRow
                key={index}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell sx={{ color: "success.main" }} align="center">
                  {asks.price}
                </TableCell>
                <TableCell align="center">{asks.size}</TableCell>
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default OrderBook;
